import React, { useState, useEffect } from "react";
import axios from "axios";
import { Box } from "@mui/material";

import Tabledata from "./Tabledata";
import Search from "./Search";
import DeleteIcon from "@mui/icons-material/Delete";
import { CircularProgress } from "@mui/material";

const Dashboard = () => {
  const [user, setUser] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchItem, setSearchItem] = useState("");
  const [debounce, setDebounce] = useState();
  const [selectCheckbox, setSelectCheckbox] = useState([]);
  const [selectAllUser, setSelectAllUser] = useState(false);
  const [loading, setLoading] = useState(true);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const data = await axios.get(
        "https://geektrust.s3-ap-southeast-1.amazonaws.com/adminui-problem/members.json"
      );
      setUser(data.data);
      setLoading(false);
      return data.data;
    } catch (error) {
      throw error;
    }
  };

  useEffect(() => {
    fetchUsers();
    // eslint-disable-next-line
  }, []);

  const rowsInPage = 10;
  const pages = Math.ceil(user.length / rowsInPage);
  const startIndex = (currentPage - 1) * rowsInPage;
  const endIndex = startIndex + rowsInPage;
  const rowsToShow = user.slice(startIndex, endIndex);

  const goBack = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const goToFirst = () => {
    setCurrentPage(1);
  };

  const goToLast = () => {
    setCurrentPage(pages);
  };

  const goNext = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const goToPage = (page) => {
    setCurrentPage(page);
  };

  const showPageButtons = () => {
    const buttons = [];
    for (let page = 1; page <= pages; page++) {
      buttons.push(
        <button
          key={page}
          onClick={() => goToPage(page)}
          disabled={currentPage === page}
        >
          {page}
        </button>
      );
    }
    return buttons;
  };

  const debounceSearch = (event, debounceTimeout) => {
    setLoading(true);
    if (debounce) {
      clearTimeout(debounce);
    }
    const debounceCall = setTimeout(() => {
      const searchResult = user.filter((item) => {
        console.log(item.name.toLowerCase());
        return (
          item.name.toLowerCase().includes(searchItem.toLowerCase()) ||
          item.email.toLowerCase().includes(searchItem.toLowerCase()) ||
          item.role.toLowerCase().includes(searchItem.toLowerCase())
        );
      });
      if (event.target.value === "") {
        setUser(fetchUsers());
      }
      setUser(searchResult);
    }, debounceTimeout);
    setDebounce(debounceCall);
    setLoading(false);
  };

  const search = (e) => {
    setSearchItem(e.target.value);
    debounceSearch(e, 500);
  };

  const handleDeleteUser = (userId) => {
    setLoading(true);
    setUser((prevUser) => prevUser.filter((user) => user.id !== userId));
    setLoading(false);
  };
  const handleUpdate = (userId, updatedUserData) => {
    // Implement the logic to update the user data in your state
    // This could involve updating an array of users or some other data structure
    setUser((prevUser) =>
      prevUser.map((user) =>
        user.id === userId ? { ...user, ...updatedUserData } : user
      )
    );
    console.log(`Update user with ID ${userId} and data:`, updatedUserData);
  };
  const handleSelectUser = (userId) => {
    let arr;
    if (selectCheckbox.includes(userId)) {
      arr = selectCheckbox.filter((user) => {
        return user !== userId;
      });
    } else {
      arr = [...selectCheckbox, userId];
    }

    setSelectCheckbox(arr);
  };

  const deleteMultiple = () => {
    setLoading(true);
    setSelectAllUser(false);
    for (let i = 0; i < selectCheckbox.length; i++) {
      handleDeleteUser(selectCheckbox[i]);
    }
    setSelectCheckbox([]);
    setLoading(false);
  };
  const selectAll = () => {
    if (selectCheckbox.length !== 10) {
      setSelectAllUser(true);
      setSelectCheckbox(
        rowsToShow.map((user) => {
          return user.id;
        })
      );
    } else {
      setSelectAllUser(false);
      setSelectCheckbox([]);
    }
    setLoading(false);
  };

  return (
    <>
    <Search searchQuery={search} />
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center", marginBottom: "2rem" }}>
      <table style={{ marginBottom: "1rem" }}>
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selectAllUser}
                onClick={selectAll}
              />
            </th>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th style={{ display: "flex", gap: "0.5vw" }}>
              Actions
              <DeleteIcon fontSize={"small"} onClick={deleteMultiple} />
            </th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <Box position={"absolute"} top={"30vh"}>
              <CircularProgress variant="secondary" />
            </Box>
          ) : (
            rowsToShow.map((val) => (
              <Tabledata
                user={val}
                key={val.id}
                checked={selectCheckbox.includes(val.id)}
                selectUser={() => handleSelectUser(val.id)}
                onDelete={() => handleDeleteUser(val.id)}
                onUpdate={() => handleUpdate(val.id)}
              />
            ))
          )}
        </tbody>
      </table>
      </div>

        <div className="button-group" style={{ alignItems: "center"}}>

            <div className="delete-selected-button">
                <button onClick={deleteMultiple} disabled={selectCheckbox.length === 0}>
                  Delete Selected
                </button>
            </div>

          <button onClick={goToFirst} disabled={currentPage === 1}>
            {"<<"}
          </button>
          <button onClick={goBack} disabled={currentPage === 1}>
            {"<"}
          </button>
          {showPageButtons()}
          <button onClick={goNext} disabled={currentPage === pages}>
            {">"}
          </button>
          <button onClick={goToLast} disabled={currentPage === pages}>
            {">>"}
          </button>
        </div>

  </>
  )
};

export default Dashboard;
